    public static List<String> GetFuseList() { return allItems; }
    public static int GetCustomModelData(Item baseItem, Item fuseItem) {
        // modifier
        int offset = 0;
        if (baseItem.equals(Items.ARROW)) { offset = 1; }
        else if (baseItem.equals(Items.DIAMOND_SWORD)) { offset = 433; }
        else if (baseItem.equals(Items.GOLDEN_SWORD)) { offset = 865; }
        else if (baseItem.equals(Items.IRON_SWORD)) { offset = 1297; }
        else if (baseItem.equals(Items.NETHERITE_SWORD)) { offset = 1729; }
        else if (baseItem.equals(Items.STONE_SWORD)) { offset = 2161; }
        else if (baseItem.equals(Items.WOODEN_SWORD)) { offset = 2593; }

        return allItems.indexOf(Registries.ITEM.getId(fuseItem).toString())+offset;
    }

    public static boolean IsInList(ItemStack stack) {
        String itemID = Registries.ITEM.getId(stack.getItem()).toString();
        return allItems.contains(itemID);
    }


